        camera.follow(hero);

        // Alle objecten toevoegen aan de wereld: camera, main karakter en mogelijke enemies
        addObject(camera, 1200, 2700);

        addObject(hero, 1120, 2695);
        addObject(enemy, 1600, 2088); 

        
