    @Override
    public void act() {
        ce.update();
        //objectSpawn();
    }
    
    private void resetStaticVariables() {
        //Alle Hero static variabelen
        Hero.bonusVelocityX = 0;
        Hero.bonusVelocityY = 0;
        
        //Alle checkpoint static variabelen
        Checkpoints.checkpointX = 1120;
        Checkpoints.checkpointY = 2695;
        
    }

